export function createGrid(rows, columns) {
  let grid = [];
  let i = 0;
  while (i < rows) {
    grid.push(Array(columns).fill(null));
    i++;
  }
  grid.push(Array(columns).fill("base"));
  return grid;
}

export function checkResult(grid) {
  for (let x in grid) {
    for (let y in grid[x]) {
      let value = grid[x][y];
      if (
        value
        //   &&
        // value === grid[x][y + 1] &&
        // value === grid[x][y + 2] &&
        // value === grid[x][y + 3]
      ) {
        console.log(grid[x][parseInt(y + 1)]);
      }
    }
  }
}
