import React, { useEffect, useState } from "react";
import { Button, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import { createGrid } from "./help";
// import { emptyIndexies, winning, minimax } from "./minimax";

export default function Game(props) {
  // Create grid
  // States and flag
  const [grid, setGrid] = useState(createGrid(6, 7));
  const [stepNumber, setStepNumber] = useState(0);
  const [huIsNext, setHuIsNext] = useState(true);
  const [huMoves, recordHuMoves] = useState([]); //For debug
  let turn = huIsNext ? "Your turn" : "Peanutbot's turn";

  //  Check for winners and tie
  //   let result = checkResult(newGrid, winningArrays);
  //   if (result === "huPlayer") result = "You win!";
  //   if (result === "aiPlayer") result = "Peanutbot wins!";
  //   if (result === "tie") result = "Draw!";
  //   if (result) turn = null;

  // const aiMove = minimax(winningArrays, newGrid, "aiPlayer");
  // console.log({ aiMove });

  // MINIMAX
  // board, winningarrays, player
  // Make a move
  const handleClick = (x, y) => {
    // if (!result) {
    const previousGrid = grid.slice();
    const currentGrid = grid[previousGrid.length - 1];
    // if (current[i + 7] !== null && current[i] === null) {
    //   recordHuMoves((moves) => [...moves, i + ","]); //For debug
    //   current[i] = huIsNext ? "huPlayer" : "aiPlayer";
    //   setMoves([previousMove[previousMove.length - 1], current]);
    //   setStepNumber(moves.length);
    //   setHuIsNext(!huIsNext);
    //   console.log({ moves });
    // }
    // }
  };

  // function undo(moves) {
  // setStepNumber(stepNumber - 1);
  // setHuIsNext(stepNumber % 2 === 0);
  // setMoves(moves[stepNumber]);
  // }

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      <div id="grid">
        {grid.map((row, x) =>
          row[x].map((status, y) => (
            <SquareGrid
              key={x + y}
              value={status}
              onClick={() => handleClick(x, y)}
            />
          ))
        )}
      </div>
      {/* Who's turn */}
      <span className={huIsNext ? "huTurn" : "aiTurn"}>{turn}</span>
      <br />
      {/* <span>{result}</span> */}

      {/* <button onClick={() => undo(moves)}>Undo</button> */}

      {/* <Link to="/single-player" className="btn btn-primary w-100 mt-3">
        Replay
      </Link> */}
      <Button onClick={handleReplay}>Replay</Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
      {/* Record moves */}
      <p>{huMoves}</p>
    </>
  );
}
