export function createGrid(rows, columns) {
  let grid = [];
  let i = 0;
  while (i < rows) {
    grid.push(Array(columns).fill(null));
    i++;
  }
  grid.push(Array(columns).fill("base"));
  return grid;
}

export function checkResult(grid) {
  if (!grid) return;
  const tie = grid.every((row) => !row.includes(null));
  if (tie) return "Draw!";

  for (let x in grid) {
    x = parseInt(x);
    for (let y in grid[x]) {
      y = parseInt(y);
      let value = grid[x][y];

      if (value === "base") continue;
      if (
        //left and right
        (value &&
          value === grid[x][y + 1] &&
          value === grid[x][y + 2] &&
          value === grid[x][y + 3]) ||
        //up and down
        (value &&
          value === grid[x + 1][y] &&
          value === grid[x + 2][y] &&
          value === grid[x + 3][y]) ||
        //diagonal - left to right (\)
        (value &&
          value === grid[x + 1][y + 1] &&
          value === grid[x + 2][y + 2] &&
          value === grid[x + 3][y + 3]) ||
        //diagonal - right to left (/)
        (value &&
          value === grid[x + 1][y - 1] &&
          value === grid[x + 2][y - 2] &&
          value === grid[x + 3][y - 3])
      ) {
        return value + " wins";
      }
    }
  }
}

export function placeAValidMove(grid, player, rowIndex, columnIndex) {
  let r = rowIndex;
  let c = columnIndex;
  if (grid[r + 1][c] !== null && grid[r][c] === null) {
    let newGrid = grid.slice();
    newGrid[r][c] = player;
    return newGrid;
  }
}
