import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import { createGrid, checkResult, findAValidMove, findAiMove } from "./help";
import "./multiPlayer.css";

export default function SinglePlayer() {
  const initialGrid = createGrid(6, 7);
  const [grid, setGrid] = useState(initialGrid);
  const [huPlayerIsNext, setHuPlayerIsNext] = useState(true);
  const [warning, displayWarning] = useState("");
  const [result, displayResult] = useState("");
  const [turn, switchTurn] = useState(true);
  let huPlayer = "Player-1";
  let aiPlayer = "Player-2";

  useEffect(() => {
    if (!huPlayerIsNext) {
      let newGrid = grid.slice();
      const [aiMoveRowIndex, aiMoveColumnIndex] = findAiMove(
        newGrid,
        huPlayer,
        aiPlayer
      );
      newGrid[aiMoveRowIndex][aiMoveColumnIndex] = aiPlayer;
      let result = checkResult(newGrid);
      displayResult(result);
      if (!result) {
        setHuPlayerIsNext(true);
      }
    }
  }, [turn]);

  const handleClick = (rowIndex, columnIndex) => {
    if (!result && huPlayerIsNext) {
      let newGrid = grid.slice();
      newGrid = findAValidMove(grid, huPlayer, rowIndex, columnIndex);
      if (!newGrid) {
        displayWarning("Invalid Move!");
      } else {
        displayWarning("");
        let result = checkResult(newGrid);
        displayResult(result);
        if (!result) {
          setHuPlayerIsNext(!huPlayerIsNext);
          switchTurn(!turn);
        }
      }
    }
  };

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      {/* GRID */}
      <div id="grid" className="w-100 bg-blue">
        {grid.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            {row.map((value, columnIndex) => (
              <SquareGrid
                key={columnIndex}
                value={value}
                rowIndex={rowIndex}
                columnIndex={columnIndex}
                handleClick={handleClick}
              />
            ))}
          </div>
        ))}
      </div>

      {/* WHO's TURN DISPLAY*/}
      <span className="turn-display">
        {result ? "" : huPlayerIsNext ? "Your turn" : "Peanutbot's turn"}
      </span>
      <br />
      {/* WARNING MSG*/}
      <span>{warning}</span>
      <br />
      {/* WINNING RESULT*/}
      <span>{result}</span>
      <br />
      <Button className="btn-warning" onClick={handleReplay}>
        Replay
      </Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
    </>
  );
}
