import React, { useEffect, useState } from "react";
import SquareGrid from "./SquareGrid";
// import { winningArrays, checkResult } from "./helper";
// import { emptyIndexies, winning, minimax } from "./minimax";

export default function Game(props) {
  let option = props.match.path.split("/")[2];

  // Create grid
  let playSquares = Array(42).fill(null);
  let hiddenSquares = Array(7).fill("base");
  let squares = playSquares.concat(hiddenSquares);

  // States and flag
  const [moves, setMoves] = useState([squares]);
  const [stepNumber, setStepNumber] = useState(0);
  const [huIsNext, setHuIsNext] = useState(true);
  let turn = huIsNext ? "Your turn" : "Peanutbot's turn";
  const [huMoves, recordHuMoves] = useState([]); //For testing

  const newGrid = moves[stepNumber];
  console.log({ newGrid });

  //  Check for winners and tie
  //   let result = checkResult(newGrid, winningArrays);
  //   if (result === "huPlayer") result = "You win!";
  //   if (result === "aiPlayer") result = "Peanutbot wins!";
  //   if (result === "tie") result = "Draw!";
  //   if (result) turn = null;

  // const aiMove = minimax(winningArrays, newGrid, "aiPlayer");
  // console.log({ aiMove });

  // MINIMAX
  // board, winningarrays, player

  // Make a move
  const handleClick = (i) => {
    // if (!result) {
    const previousMove = moves.slice();
    const current = moves[stepNumber];
    if (current[i + 7] !== null && current[i] === null) {
      recordHuMoves((moves) => [...moves, i + ","]); //For testing
      current[i] = huIsNext ? "huPlayer" : "aiPlayer";
      setMoves([...previousMove, current]);
      setStepNumber(moves.length);
      setHuIsNext(!huIsNext);
    }
    // }
  };

  // function undo(moves) {
  // setStepNumber(stepNumber - 1);
  // setHuIsNext(stepNumber % 2 === 0);
  // setMoves(moves[stepNumber]);
  // }

  function replay() {
    window.location.reload(false);
  }

  return (
    <>
      <div id="grid">
        {moves[stepNumber].map((move, i) => (
          <SquareGrid key={i} value={move} onClick={() => handleClick(i)} />
        ))}
      </div>
      {/* Who's turn */}
      <span className={huIsNext ? "huTurn" : "aiTurn"}>{turn}</span>
      <br />
      <span>{result}</span>

      {/* <button onClick={() => undo(moves)}>Undo</button> */}
      <button onClick={replay}>Replay</button>
      <br />
      {/* <QuitButton userInfo={props} /> */}
      <br />
      {/* Record moves */}
      <p>{huMoves}</p>
    </>
  );
}
