// NOTE
// r = row index
// c = column index

export function createGrid(r, c) {
  let grid = [];
  let i = 0;
  while (i < r) {
    grid.push(Array(c).fill(null));
    i++;
  }
  grid.push(Array(c).fill("base"));
  return grid;
}

export function checkResult(grid) {
  // for (let x in grid) {
  //   x = parseInt(x);
  //   for (let y in grid[x]) {
  //     y = parseInt(y);
  for (let r = 0; r < grid.length; r++) {
    for (let c = 0; c < grid[r].length; c++) {
      let value = grid[r][c];
      console.log({ value });

      // if (value === "base") continue;
      if (
        //left and right
        (value &&
          value === grid[r][c + 1] &&
          value === grid[r][c + 2] &&
          value === grid[r][c + 3]) ||
        //up and down
        (value &&
          value === grid[r + 1][c] &&
          value === grid[r + 2][c] &&
          value === grid[r + 3][c]) ||
        //diagonal " \ "
        (value &&
          value === grid[r + 1][c + 1] &&
          value === grid[r + 2][c + 2] &&
          value === grid[r + 3][c + 3]) ||
        //diagonal " / "
        (value &&
          value === grid[r + 1][c - 1] &&
          value === grid[r + 2][c - 2] &&
          value === grid[r + 3][c - 3])
      ) {
        return value + " wins";
      }
      const tie = grid.every((row) => !row.includes(null));
      if (tie) return "Draw!";
    }
  }
}

export function findAValidMove(grid, player, r, c) {
  if (grid[r][c] === "base") {
    return null;
  }
  if (grid[r + 1][c] !== null && grid[r][c] === null) {
    grid[r][c] = player;
    return grid;
  }
  return null;
}

export function findAiMove(grid, huPlayer, aiPlayer) {
  let MAX_DEPTH = 5;
  let bestMoveScore = 100;
  let move = [];

  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, aiPlayer, r, c);
      if (newGrid) {
        const moveScore = maxScore(grid, MAX_DEPTH, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
          move = [r, c];
        }
      }
    }
  }
  console.log("found move");
  return move;
}

function minScore(grid, depth, huPlayer, aiPlayer) {
  let result = checkResult(grid);
  if (result) {
    console.log("result");
    if (result === "Player-1 wins") return 10;
    if (result === "Player-2 wins") return -10;
    if (result === "Draw") return 0;
  }
  if (depth === 0) return 0;

  let bestMoveScore = 100;
  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, aiPlayer, r, c);
      if (newGrid) {
        const moveScore = maxScore(grid, depth - 1, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
        }
      }
    }
  }
  return bestMoveScore;
}

function maxScore(grid, depth, huPlayer, aiPlayer) {
  let result = checkResult(grid);
  if (result) {
    console.log("result");
    if (result === "Player-1 wins") return 10;
    if (result === "Player-2 wins") return -10;
    if (result === "Draw") return 0;
  }
  if (depth === 0) return 0;

  let bestMoveScore = -100;
  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, huPlayer, r, c);
      if (newGrid) {
        const moveScore = minScore(grid, depth - 1, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore > bestMoveScore) {
          bestMoveScore = moveScore;
        }
      }
    }
  }
  return bestMoveScore;
}
