import React, { useState, useEffect, useMemo } from "react";
import { Button } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import { createGrid, checkResult } from "./help";
import "./multiPlayer.css";
import io from "socket.io-client";

export default function MultiPlayer() {
  console.log("BEGIN");

  const history = useHistory();
  const blankGrid = createGrid(6, 7);
  const [grid, setGrid] = useState(blankGrid);
  const [connected1, toggleConnection1] = useState(false);
  const [connected2, toggleConnection2] = useState(false);
  const [player1IsNext, setPlayer1IsNext] = useState(true);
  let currentTurn = player1IsNext ? 1 : 2;
  // const [player1, setUser1] = useState(false);
  // const [player2, setUser2] = useState(false);
  let player = 1;
  useEffect(() => {
    const client = io("http://localhost:3001");

    // client.on("this-is-player2", () => {
    //   toggleReady(!ready);
    // });

    client.on("player-num", (num) => {
      if (num === -1) {
        alert("Sorry, server is full!");
        history.push("/");
      }
      if (num === 0) {
        //when 1 leave and return, and tthere is 2
        toggleConnection1(true);
        // setUser1(true);
        console.log("i am player 1");
        console.log({ player });
      }
      if (num === 1) {
        toggleConnection1(true);
        toggleConnection2(true);
        // setUser2(true);
        player = 2;
        console.log("i am player 2, turnning off my ready");
        console.log({ player });
      }
    });

    client.on("inform-player1", () => {
      toggleConnection2(true);
    });

    client.on("inform-player2", () => {
      toggleConnection1(true);
    });

    // client.on("connection-status", (status) => {
    //   if (status[0]) {
    //     toggleConnection1(true);
    //   }
    //   if (status[1]) {
    //     toggleConnection2(true);
    //   }
    // });

    // client.on("server-full", () => {
    //   alert("Sorry, server is full!");
    //   history.push("/");
    // });

    client.on("player-disconnected", (num) => {
      if (num === 0) toggleConnection1(false);
      if (num === 1) toggleConnection2(false);
    });

    // client.emit("switch-turn", () => {
    //   toggleReady(!ready);
    //   console.log("turnning off my ready, and emit to server ");
    //   console.log({ ready });
    // });

    // client.on("take-the-turn", () => {
    //   toggleReady(!ready);
    //   console.log("my run, turning on my ready");
    //   console.log({ ready });
    // });

    client.emit("grid", grid);

    client.on("new-grid", (newGrid) => {
      setGrid(newGrid);
    });

    return () => {
      client.disconnect();
    };
  }, [player1IsNext]);

  let resultDisplay;
  // let result = checkResult(grid);
  // if (result === "huPlayer") resultDisplay = "You win!";
  // if (result === "aiPlayer") resultDisplay = "Peanutbot wins!";
  // if (result === "tie") resultDisplay = "Draw!";
  console.log({ player });
  console.log({ currentTurn });
  const handleClick = (rowIndex, columnIndex) => {
    // both players present??
    if (currentTurn === player) {
      // console.log({ player2 });

      let r = rowIndex;
      let c = columnIndex;

      if (grid[r + 1][c] !== null && grid[r][c] === null) {
        let newGrid = grid.slice();
        newGrid[r][c] = player1IsNext ? "player1" : "player2";
        setGrid(newGrid);
        setPlayer1IsNext(!player1IsNext);
      }
    }
  };

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      {/* GRID */}
      <div id="grid">
        {grid.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            {row.map((value, columnIndex) => (
              <SquareGrid
                key={columnIndex}
                value={value}
                rowIndex={rowIndex}
                columnIndex={columnIndex}
                handleClick={handleClick}
              />
            ))}
          </div>
        ))}
      </div>

      {/* CONNECTION INDICATOR */}

      <div className="player p1">
        Player 1
        <div className="connected">
          Connected
          <span className={`indicator ${connected1 ? "green" : "red"}`} />
        </div>
      </div>

      <div className="player p2">
        Player 2
        <div className="connected">
          Connected
          <span className={`indicator ${connected2 ? "green" : "red"}`} />
        </div>
      </div>

      {/* WHO's TURN    result ? "" :     */}
      <span className={player1IsNext ? "player1Turn" : "player2Turn"}>
        {player1IsNext ? "Player1's turn" : "Player2's turn"}
      </span>
      <br />

      <span>{resultDisplay}</span>

      <Button>Replay</Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
      {/* Record moves */}
      {/* <p>{huMoves}</p> */}
    </>
  );
}
