import React from "react";

export default function SquareGrid({
  value,
  rowIndex,
  columnIndex,
  handleClick,
}) {
  return (
    <div className={`${value === "base" ? value : "square"}`}>
      <div
        className={`${
          value !== null && value !== "base" ? "circle " + value : ""
        }`}
        onClick={() => handleClick(rowIndex, columnIndex)}
      ></div>
    </div>
  );
}
