export function createGrid(rows, columns) {
  let grid = [];
  let i = 0;
  while (i < rows) {
    grid.push(Array(columns).fill(null));
    i++;
  }
  grid.push(Array(columns).fill("base"));
  return grid;
}

export function checkResult(grid) {
  if (!grid) return;
  const tie = grid.every((row) => !row.includes(null));
  if (tie) return "Draw!";

  for (let x in grid) {
    x = parseInt(x);
    for (let y in grid[x]) {
      y = parseInt(y);
      let value = grid[x][y];

      if (value === "base") continue;
      if (
        //left and right
        (value &&
          value === grid[x][y + 1] &&
          value === grid[x][y + 2] &&
          value === grid[x][y + 3]) ||
        //up and down
        (value &&
          value === grid[x + 1][y] &&
          value === grid[x + 2][y] &&
          value === grid[x + 3][y]) ||
        //diagonal - left to right (\)
        (value &&
          value === grid[x + 1][y + 1] &&
          value === grid[x + 2][y + 2] &&
          value === grid[x + 3][y + 3]) ||
        //diagonal - right to left (/)
        (value &&
          value === grid[x + 1][y - 1] &&
          value === grid[x + 2][y - 2] &&
          value === grid[x + 3][y - 3])
      ) {
        return value + " wins";
      }
    }
  }
}

export function findAValidMove(grid, player, rowIndex, columnIndex) {
  let r = rowIndex;
  let c = columnIndex;
  // console.log({ rowIndex });
  // console.log({ columnIndex });
  if (grid[r][c] === "base") {
    return null;
  }
  if (grid[r + 1][c] !== null && grid[r][c] === null) {
    let newGrid = grid.slice();
    newGrid[r][c] = player;
    return newGrid;
  }
  return null;
}

export function findAiMove(grid, huPlayer, aiPlayer) {
  let result = checkResult(grid);
  if (result) return;

  let bestMoveScore = 100;
  let move = [];

  for (let rowIndex in grid) {
    rowIndex = parseInt(rowIndex);

    for (let columnIndex in grid[rowIndex]) {
      columnIndex = parseInt(columnIndex);

      let newGrid = findAValidMove(grid, aiPlayer, rowIndex, columnIndex);

      if (newGrid) {
        const moveScore = maxScore(newGrid, huPlayer, aiPlayer);
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
          move[0] = rowIndex;
          move[1] = columnIndex;
        }
      }
    }
  }
  console.log({ move });

  return move;
}

function minScore(grid, huPlayer, aiPlayer) {
  let result = checkResult(grid);

  if (result === "Player-1 wins") {
    // console.log("min checked1");

    return 10;
  }
  if (result === "Player-2 wins") {
    // console.log("min checked2");

    return -10;
  }
  if (result === "Draw") {
    // console.log("min checked3");

    return 0;
  }

  let bestMoveScore = 100;
  for (let rowIndex in grid) {
    rowIndex = parseInt(rowIndex);
    for (let columnIndex in grid[rowIndex]) {
      columnIndex = parseInt(columnIndex);
      let newGrid = findAValidMove(grid, aiPlayer, rowIndex, columnIndex);
      if (newGrid) {
        const moveScore = maxScore(newGrid, huPlayer, aiPlayer);
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
        }
      }
    }
  }
  return bestMoveScore;
}

function maxScore(grid, huPlayer, aiPlayer) {
  let result = checkResult(grid);

  if (result === "Player-1 wins") {
    // console.log("max checked");

    return 10;
  }
  if (result === "Player-2 wins") {
    // console.log("max checked");

    return -10;
  }
  if (result === "Draw") {
    // console.log("max checked");

    return 0;
  }

  let bestMoveScore = -100;

  // let move;
  for (let rowIndex in grid) {
    rowIndex = parseInt(rowIndex);
    for (let columnIndex in grid[rowIndex]) {
      columnIndex = parseInt(columnIndex);
      let newGrid = findAValidMove(grid, huPlayer, rowIndex, columnIndex);
      if (newGrid) {
        const moveScore = minScore(newGrid, huPlayer, aiPlayer);
        // console.log({ moveScore });

        if (moveScore > bestMoveScore) {
          bestMoveScore = moveScore;
          // console.log({ bestMoveScore });
        }
      }
    }
  }
  return bestMoveScore;
}
