import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import { createGrid, checkResult } from "./help";
import "./multiPlayer.css";
import io from "socket.io-client";

export default function MultiPlayer() {
  const history = useHistory();
  const blankGrid = createGrid(6, 7);
  const [grid, setGrid] = useState(blankGrid);
  const [connected1, toggleConnection1] = useState(false);
  const [connected2, toggleConnection2] = useState(false);
  const [endOfMyTurn, triggerEndOfMyTurn] = useState();
  const [ready, toggleReady] = useState(false);
  const [currentPlayer, setCurrentPlayer] = useState(1);

  useEffect(() => {
    const client = io("http://localhost:3001");

    // ignore the 3rd player
    client.on("server-full", () => {
      alert("Sorry, server is full!");
      history.push("/");
    });

    client.on("assign-player-as-player2", () => {
      setCurrentPlayer(2);
      // toggleReady(!ready);
    });

    // set connection status indicators
    client.on("player-connection-status", (status) => {
      if (status[0]) toggleConnection1(true);
      if (status[1]) toggleConnection2(true);
    });

    // switch turns
    client.emit("switch-turn", grid);
    client.on("take-the-turn", (grid) => {
      setGrid(grid);
      toggleReady(true);
    });

    // handle disconnect
    client.on("player-disconnected", (num) => {
      if (num === 0) toggleConnection1(false);
      if (num === 1) toggleConnection2(false);
    });

    return () => {
      client.disconnect();
    };
  }, [endOfMyTurn]);

  let resultDisplay;
  let result = checkResult(grid);

  if (result === "player1") resultDisplay = "Player 1 wins!";
  if (result === "player2") resultDisplay = "Player 2 wins!";
  if (result === "tie") resultDisplay = "Draw!";

  const handleClick = (rowIndex, columnIndex) => {
    if (ready) {
      let r = rowIndex;
      let c = columnIndex;
      if (grid[r + 1][c] !== null && grid[r][c] === null) {
        let newGrid = grid.slice();
        newGrid[r][c] = currentPlayer === 1 ? "player1" : "player2";
        toggleReady(!ready);
        setGrid(newGrid);
        triggerEndOfMyTurn(!endOfMyTurn);
      }
    }
  };

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      {/* GRID */}
      <div id="grid">
        {grid.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            {row.map((value, columnIndex) => (
              <SquareGrid
                key={columnIndex}
                value={value}
                rowIndex={rowIndex}
                columnIndex={columnIndex}
                handleClick={handleClick}
              />
            ))}
          </div>
        ))}
      </div>

      {/* CONNECTION INDICATOR */}
      <div className="player p1">
        Player 1
        <div className="connected">
          Connected
          <span className={`indicator ${connected1 ? "green" : "red"}`} />
        </div>
      </div>

      <div className="player p2">
        Player 2
        <div className="connected">
          Connected
          <span className={`indicator ${connected2 ? "green" : "red"}`} />
        </div>
      </div>

      {/* WHO's TURN DISPLAY*/}
      <span className="turn-display">
        {result ? "" : ready ? "your turn" : "waiting for the other player..."}
      </span>
      <br />
      {/* WINNING RESULT*/}
      <span>{resultDisplay}</span>
      <br />
      <Button onClick={handleReplay}>Replay</Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
    </>
  );
}
