export function createGrid(rows, columns) {
  let grid = [];
  let i = 0;
  while (i < rows) {
    grid.push(Array(columns).fill(null));
    i++;
  }
  grid.push(Array(columns).fill("base"));
  return grid;
}

export function checkResult(grid) {
  for (let row of grid) {
    for (let index in row) {
      let value = row[index];
      if (
        value &&
        value === row[index + 1] &&
        value === row[index + 2] &&
        value === row[index + 3]
      ) {
        console.log("found win");
      }
    }
  }
}
