import React, { useState, useEffect, useRef } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import {
  createGrid,
  checkResult,
  findAValidMove,
  findAiMove,
  minScore,
  maxScore,
} from "./help";
import "./multiPlayer.css";

export default function SinglePlayer() {
  const blankGrid = createGrid(6, 7);
  const [grid, setGrid] = useState(blankGrid);
  const [huPlayerIsNext, setHuPlayerIsNext] = useState(true);
  const [warning, displayWarning] = useState("");
  const [result, displayResult] = useState("");
  const [turn, triggerTurn] = useState();
  const prevGridRef = useRef();

  let huPlayer = "Player-1";
  let aiPlayer = "Player-2";

  // useEffect(() => {

  //   setGrid(prevGrid);
  // }, [turn]);

  const handleClick = (rowIndex, columnIndex) => {
    prevGridRef.current = grid;
    const prevGrid = prevGridRef.current;
    console.log({ prevGrid });

    if (!result && huPlayerIsNext) {
      // let newGrid = grid.slice();
      let newGrid = findAValidMove(grid, huPlayer, rowIndex, columnIndex);
      if (!newGrid) {
        displayWarning("Invalid Move!");
      } else {
        displayWarning("");
        setGrid(newGrid);
        displayResult(checkResult(newGrid));

        if (!result) {
          setHuPlayerIsNext(!huPlayerIsNext);
          let copy = newGrid.slice();
          const [aiMoveRowIndex, aiMoveColumnIndex] = findAiMove(
            copy,
            huPlayer,
            aiPlayer
          );
          console.log("triggered");

          setGrid(prevGrid);
          triggerTurn(!turn);
        }
        // }
        // console.log({  });
        // console.log({ huPlayerIsNext });
        // console.log({ newGrid });
        // console.log({ test });
        // setGrid(test);
      }
    }
  };

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      {/* GRID */}
      <div id="grid">
        {grid.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            {row.map((value, columnIndex) => (
              <SquareGrid
                key={columnIndex}
                value={value}
                rowIndex={rowIndex}
                columnIndex={columnIndex}
                handleClick={handleClick}
              />
            ))}
          </div>
        ))}
      </div>

      {/* WHO's TURN DISPLAY*/}
      <span className="turn-display">
        {result ? "" : huPlayerIsNext ? "Your turn" : "Peanutbot's turn"}
      </span>
      <br />
      {/* WARNING MSG*/}
      <span>{warning}</span>
      <br />
      {/* WINNING RESULT*/}
      <span>{result}</span>
      <br />
      <Button onClick={handleReplay}>Replay</Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
    </>
  );
}
