import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import SquareGrid from "./SquareGrid";
import { createGrid, checkResult } from "./help";
import "./multiPlayer.css";
import io from "socket.io-client";

const socket = io.connect("http://localhost:3001");

let playerNum = 0;

export default function MultiPlayer(props) {
  const [currentPlayerIs1, setCurrentPlayer] = useState(true);

  useEffect(() => {
    socket.on(
      "player-number",
      (num) => {
        if (num === -1) {
          console.log("Sorry, the server is full");
        } else {
          playerNum = parseInt(num);
          if (playerNum === 1) {
            setCurrentPlayer(!currentPlayerIs1);
          }

          console.log({ playerNum });

          // Get other player status
          socket.emit("check-players");
        }
      },
      []
    );

    // Another player has connected or disconnected
    socket.on("player-connection", (num) => {
      console.log(`Player number ${num} has connected or disconnected`);
      // playerConnectedOrDisconnected(num)
    });
  });

  const grid = createGrid(6, 7);
  const [newGrid, setGrid] = useState(grid);
  //   const [stepNumber, setStepNumber] = useState(0);
  const [player1IsNext, setPlayer1IsNext] = useState(true);
  //   const [huMoves, recordHuMoves] = useState([]); //For debug
  let turn = player1IsNext ? "Player1's turn" : "Player2's turn";

  // let resultDisplay
  //   let result = checkResult(newGrid);
  //   if (result === "huPlayer") resultDisplay = "You win!";
  //   if (result === "aiPlayer") resultDisplay = "Peanutbot wins!";
  //   if (result === "tie") resultDisplay = "Draw!";

  const handleClick = (rowIndex, columnIndex) => {
    // if (!result) {
    let r = rowIndex;
    let c = columnIndex;

    if (newGrid[r + 1][c] !== null && newGrid[r][c] === null) {
      // recordHuMoves((moves) => [...moves, "(" + r + "," + c + ")"]); //For debug
      newGrid[r][c] = player1IsNext ? "player1" : "player2";
      setPlayer1IsNext(!player1IsNext);
    }
    // }
  };

  function handleReplay() {
    window.location.reload(false);
  }

  return (
    <>
      <div id="grid">
        {newGrid.map((row, rowIndex) => (
          <div className="row" key={rowIndex}>
            {row.map((value, columnIndex) => (
              <SquareGrid
                key={columnIndex}
                value={value}
                rowIndex={rowIndex}
                columnIndex={columnIndex}
                handleClick={handleClick}
              />
            ))}
          </div>
        ))}
      </div>

      {/* Who's turn */}
      <span className={player1IsNext ? "player1Turn" : "player2Turn"}>
        {/* {result ? "" : turn} */}
      </span>
      <br />

      {/* <span>{resultDisplay}</span> */}
      <div>
        {currentPlayerIs1 ? "player 1 & 2 are ready" : "player 1 is ready"}
      </div>
      <Button onClick={handleReplay}>Replay</Button>
      <br />
      <Link to="/" className="btn btn-primary w-100 mt-3">
        Quit
      </Link>
      <br />
      {/* Record moves */}
      {/* <p>{huMoves}</p> */}
    </>
  );
}
