import React from "react";

export default function SquareGrid({
  value,
  rowIndex,
  columnIndex,
  handleClick,
}) {
  return (
    <div
      className={
        value === "base" ? value : "square" + " border border-primary border-50"
      }
      onClick={() => handleClick(rowIndex, columnIndex)}
    >
      <div
        className={value !== null && value !== "base" ? "circle " + value : ""}
      ></div>
    </div>
  );
}
