// NOTE
// r = row index
// c = column index
// Last row of grid has values of "base".
// When doing a loop, the last row will be skipped.

export function createGrid(r, c) {
  let grid = [];
  let i = 0;
  while (i < r) {
    grid.push(Array(c).fill(null));
    i++;
  }
  return grid;
}

export function checkResult(grid) {
  const tie = grid.every((row) => !row.includes(null));
  if (tie) return "Draw!";

  for (let r = 0; r < grid.length; r++) {
    for (let c = 0; c < grid[r].length; c++) {
      let value = grid[r][c];

      if (
        //left and right
        value &&
        value === grid[r][c + 1] &&
        value === grid[r][c + 2] &&
        value === grid[r][c + 3]
      ) {
        return value + " wins";
      }

      if (r < grid.length - 3) {
        if (
          //up and down
          (value &&
            value === grid[r + 1][c] &&
            value === grid[r + 2][c] &&
            value === grid[r + 3][c]) ||
          //diagonal " \ "
          (value &&
            value === grid[r + 1][c + 1] &&
            value === grid[r + 2][c + 2] &&
            value === grid[r + 3][c + 3]) ||
          //diagonal " / "
          (value &&
            value === grid[r + 1][c - 1] &&
            value === grid[r + 2][c - 2] &&
            value === grid[r + 3][c - 3])
        ) {
          return value + " wins";
        }
      }
    }
  }
  return null;
}

export function findAValidMove(grid, player, c) {
  for (let r = grid.length - 1; r >= 0; r--) {
    if (grid[r][c] === null) {
      let newGrid = grid.slice();
      newGrid[r][c] = player;
      return newGrid;
    }
  }
}

export function findAiMove(grid, huPlayer, aiPlayer) {
  let MAX_DEPTH = 5;
  let bestMoveScore = 100;
  let move = [];

  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, aiPlayer, c);
      if (newGrid) {
        const moveScore = maxScore(grid, MAX_DEPTH, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
          move = [r, c];
        }
      }
    }
  }
  console.log({ move });

  return move;
}

function minScore(grid, depth, huPlayer, aiPlayer) {
  let result = checkResult(grid);
  if (result) {
    console.log("result");
    if (result === "Player-1 wins") return 10;
    if (result === "Player-2 wins") return -10;
    if (result === "Draw") return 0;
  }
  if (depth === 0) return 0;

  let bestMoveScore = 100;
  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, aiPlayer, c);
      if (newGrid) {
        const moveScore = maxScore(grid, depth - 1, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore < bestMoveScore) {
          bestMoveScore = moveScore;
        }
      }
    }
  }
  return bestMoveScore;
}

function maxScore(grid, depth, huPlayer, aiPlayer) {
  let result = checkResult(grid);
  if (result) {
    console.log("result");
    if (result === "Player-1 wins") return 10;
    if (result === "Player-2 wins") return -10;
    if (result === "Draw") return 0;
  }
  if (depth === 0) return 0;

  let bestMoveScore = -100;
  for (let r = grid.length - 1; r > 0; r--) {
    for (let c = grid[r].length - 1; c > 0; c--) {
      const newGrid = findAValidMove(grid, huPlayer, c);
      if (newGrid) {
        const moveScore = minScore(grid, depth - 1, huPlayer, aiPlayer);
        grid[r][c] = null;
        if (moveScore > bestMoveScore) {
          bestMoveScore = moveScore;
        }
      }
    }
  }
  return bestMoveScore;
}
